import React, { useState } from 'react';
import Island from './components/Island';
import MessageBottle from './components/MessageBottle';
import MessageCard from './components/MessageCard';
import CategoryFilter from './components/CategoryFilter';
import useRandomSentence from './hooks/useRandomSentence';
import './styles/global.css';
import './App.css';

function App() {
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [currentSentence, setCurrentSentence] = useState(null);
  const [showMessageCard, setShowMessageCard] = useState(false);
  const { getRandomSentence } = useRandomSentence();

  const handleBottleClick = () => {
    const sentence = getRandomSentence(selectedCategory);
    setCurrentSentence(sentence);
    setShowMessageCard(true);
  };

  const handleCloseMessage = () => {
    setShowMessageCard(false);
    setTimeout(() => {
      setCurrentSentence(null);
    }, 400);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div className="App">
      <Island>
        <CategoryFilter
          selectedCategory={selectedCategory}
          onCategoryChange={handleCategoryChange}
        />

        <div className="bottle-area">
          <MessageBottle onClick={handleBottleClick} isFloating={true} />
          <div className="instruction">
            <p className="instruction-text">üçæ Ïú†Î¶¨Î≥ëÏùÑ ÌÅ¥Î¶≠Ìï¥ÏÑú Î¨∏Ïû•ÏùÑ Î∞õÏïÑÎ≥¥ÏÑ∏Ïöî</p>
          </div>
        </div>
      </Island>

      <MessageCard
        sentence={currentSentence}
        isVisible={showMessageCard}
        onClose={handleCloseMessage}
      />
    </div>
  );
}

export default App;
